package com.briup.estore.util;

import com.alipay.api.AlipayClient;
import com.alipay.api.DefaultAlipayClient;

public class AlipayClientUntil {
	//  支付寶網關
	private static String serverUrl = "https://openapi.alipay.com/gateway.do";
	// 應用Id每一個賣家都有一個唯一的appid
	private static String  appId = "2016101300673285";
	// 支付寶私鑰
	private static String privateKey = "";
    // 發送數據的格式
	private static String format = "json";
	// 字符編碼
	private static String charset = "utf-8";
	//支付寶公鑰
	private static String publicKey = "";
	// 支付寶簽名
	private static String signTyp ="RSA2";
	
	public static AlipayClient getAlipayClient() {
		return new DefaultAlipayClient(serverUrl, appId, privateKey,format,charset,publicKey,signTyp);
	}
	
}
